import React, { useState } from "react";
import axios from "axios";
import styles from "./ChatGPT.css";

// Navbar component
const Navbar = () => {
  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container-fluid">
        <span className="navbar-brand mb-0 h1">Navbar</span>
      </div>
    </nav>
  );
};

// Sidebar component
const Sidebar = () => {
  return (
    <div className="bg-light border-end" id="sidebar-wrapper">
      <div className="sidebar-heading">Sidebar </div>
      <div className="list-group list-group-flush">
        <a href="#" className="list-group-item list-group-item-action bg-light">Option 1</a>
        <a href="#" className="list-group-item list-group-item-action bg-light">Option 2</a>
        <a href="#" className="list-group-item list-group-item-action bg-light">Option 3</a>
      </div>
    </div>
  );
};

export default function ChatGPT() {
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState("");
  const HTTP = "http://localhost:8080/chat";

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post(`${HTTP}`, { prompt })
      .then((res) => {
        setResponse(res.data);
        console.log(prompt);
      })
      .catch((error) => {
        console.log(error);
      });

    setPrompt("");
  };

  const handlePrompt = (e) => {
    setPrompt(e.target.value);
  };

  return (
    <div>
      <Navbar />
      <div className="d-flex" id="wrapper">
        <Sidebar />
        <div className="container container-sm p-1">
          <h1 className="title text-center">ChatBot</h1>
          <form className="form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="prompt">Ask questions</label>
              <input
                id="prompt"
                className="shadow-sm"
                type="text"
                placeholder="Type your question here..."
                value={prompt}
                onChange={handlePrompt}
              />
            </div>
            <button className="btn btn-accept w-100" type="submit">
              <span className="send-btn">Send</span>
            </button>
          </form>
          <div className="bg-darkGreen mt-2 p-1 border-5">
            <p className="text-light" style={{ marginTop: "10px" }}>
              {response ? response : "Ask me anything..."}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
