import React, { useState } from "react";
import "./Chatbot.css"; // Import your CSS file for styling

const Chatbot = () => {
  const [messages, setMessages] = useState([
    {
      message: "Hello, I'm ChatGPT! Ask me anything!",
      sender: "ChatGPT",
    },
  ]);
  const [isTyping, setIsTyping] = useState(false);
  const [newMessage, setNewMessage] = useState("");

  const handleSend = () => {
    if (newMessage.trim() === "") return;
    const newMessages = [...messages, { message: newMessage, sender: "user" }];
    setMessages(newMessages);
    setNewMessage("");
    setIsTyping(true);
    processMessageToChatGPT(newMessages);
  };

  const processMessageToChatGPT = (chatMessages) => {
    // Simulate processing message with ChatGPT (Replace with your API call)
    setTimeout(() => {
      const responseMessage = {
        message: "Here's a response from ChatGPT!",
        sender: "ChatGPT",
      };
      setMessages([...chatMessages, responseMessage]);
      setIsTyping(false);
    }, 1000);
  };

  return (
    <div className="chatbot-container">
      <div className="chatArea">
        <div className="message-list">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              {message.message}
            </div>
          ))}
          {isTyping && <div className="message ChatGPT">ChatGPT is typing...</div>}
        </div>
        <div className="message-input">
          <input
            type="text"
            placeholder="Type message here"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
          />
          <button onClick={handleSend}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
