import React, { useState } from "react";

import styles from "./Signup.css";

const Signup = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    password: "",
  });
  const getUserData=(event) => {
    name = event.target.name;
    value =event.target.value;

    setUser({  ...user,[name]:value})
  };
  const postData=async(e)=>{
    e.preventDefault();
    fetch("https://signup-react-project-default-rtdb.firebaseio.com/signup-react-projectdata.json",
  {
    method:"POST",
    headers:{
      "Content-Type":"application"
    }
  });
  };

  return (
    <div>
      <div className="sign-up-part">
        <div>
          <div className="newton-imge">
            <img
              className="newton-imgee"
              src="	https://newtonslab.in/wp-content/uploads/2024/01/Logo-with-text-dark-1.png"
            ></img>
          </div>
          <div className="create-txt">
            <h>Create an Account</h>
          </div>
          <hr></hr>
          <div className="signup-container">
            {/* Input fields for name, email, and password */}
            <input
              className="elements-txt"
              type="text"
              placeholder="Full Name"
              value={user.name}
              onChange={getUserData}
            />
            <input
             className="elements-txt"
              type="email" 
              placeholder="E-mail"
              value={user.name}
              onChange={getUserData}
               />
            <input
              className="elements-txt"
              type="password"
              placeholder="Password"
              value={user.name}
              onChange={getUserData}
            />
            {/* Button to submit the signup form */}
            <button className="creat-button" onClick={postData}>Create Account</button>
            <hr></hr>
            <div className="sign-txt">
              have an account?<a href="">sign in</a>{" "}
            </div>
          </div>
        </div>
      </div>
      <div className="Newon-img-part">
        <img
          className="right-img"
          src="https://app.newtonslab.in/media/agency/customizations/login_signup/Newton-web_1_k1QCEtk.jpg"
        ></img>
      </div>
    </div>
  );
};

export default Signup;
