import React, { useState } from 'react';
import './Chatbot.css'; // Import your CSS file for styling

import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import { MainContainer, ChatContainer, MessageList, Message, MessageInput, TypingIndicator } from '@chatscope/chat-ui-kit-react';
const Chatbot = () => {
  const [messages, setMessages] = useState([]);

  const handleMessageSend = () => {
    // Code to send user message to chatbot API and receive response
    // After receiving response, update messages state with the new message
    // Example:
    // setMessages([...messages, { author: 'user', text: userInput }]);
    // setMessages([...messages, { author: 'chatbot', text: response }]);
  };

  return (
    <div className="chatbot-container">
      <div className='left-part'>
        <div className='sidebar'>
          <h1>newton</h1>
          <h1>sub admin</h1>
          <ul>
            <li>chatbot</li>
            <li>Account</li>
            <li>User</li>
            <li>Documentation</li>
            <li>Upgrade</li>
          </ul>
        </div>
      </div>
      <div className='right-part'>
        <div className='chatArea'>
          {/* Display messages */}
          {messages.map((message, index) => (
            <div key={index} className={message.author}>
              {message.text}
            </div>
          ))}
          {/* User input form */}
          <div>
            
            <input type="text" placeholder="Type your message..." />
            <button onClick={handleMessageSend}>Send</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
